frappe.ui.form.on('Patient', {
    refresh: function (frm) {
        frm.add_custom_button(__('Update Patient Details'), function () {
        	var progress_bar = '<span id="progress_bar_text">Updating...</span><div class="progress">' +
        		'<div id = "progress_bar" class="progress-bar progress-bar-info progress-bar-striped active" role="progressbar" aria-valuenow="01" aria-valuemin="0" aria-valuemax="100" style="width:100%">' +
        		'</div></div>';
        	var progress = new frappe.ui.Dialog({
        		title: "Loading...",
        		fields: [
        			{"fieldtype": "HTML", "fieldname": "progress"}]
        	});
        	progress.fields_dict.progress.$wrapper.html(progress_bar);
        	progress.show()
            frappe.call({
                'method': "healthcare_addons.public.python.patevents.update_patient_details",
                'args':{
                    doc: frm.doc
                },
                'callback': function(d){
                    if(d.message){
                        progress.hide();
                        frappe.msgprint(d.message);
                    }
                }   
            });
        });
    }
});

frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		// your code here
	},
	onload(frm){
	    practitioner_set_query(frm);
	    set_hmo_visibility(frm);
	    show_hide_ref_practitioner(frm);
	    if(frappe.user.has_role("Sales Manager")){
	       frm.set_df_property("set_posting_time", "hidden", 0);
	    }
	    else{
	        frm.set_df_property("set_posting_time", "hidden", 1);
	    }
	}
})

frappe.ui.form.on("Sales Invoice", "sales_partner", function(frm){
    frm.set_value("commission_rate",0);
    frm.set_value("total_commission",0);    
});

frappe.ui.form.on('Sales Invoice', 'ref_practitioner', function(frm){
    reset_sales_partner(frm);
    if(frm.doc.ref_practitioner)
        frappe.call({
            "method": "frappe.client.get",
            "args":{
                doctype: "Healthcare Practitioner",
                name: frm.doc.ref_practitioner
            },
            "callback":function(data){
                var practitioner_type = data.message['custom_type'];
                var practitioner_name = data.message['practitioner_name'];
                if(practitioner_type == 'Doctor (MD)'){
                    practitioner_name += ', MD';
                }
                frm.set_value("custom_practitioner_name",practitioner_name);

                if(frm.doc.custom_source.includes("HMO")||frm.doc.custom_source.includes("Doctor's Referral")||frm.doc.custom_source.includes("Promo"))
                    frm.set_value("sales_partner",data.message['custom_sales_partner']);
            }
        });
});

frappe.ui.form.on('Sales Invoice', 'custom_external_referrer', function(frm){
    reset_sales_partner(frm);
    if(frm.doc.custom_external_referrer)
        frappe.call({
            "method": "frappe.client.get",
            "args":{
                doctype: "External Referrer",
                name: frm.doc.custom_external_referrer
            },
            "callback":function(data){
                frm.set_value("sales_partner",data.message['sales_partner']);
            }
        });
});

frappe.ui.form.on('Sales Invoice', 'custom_source', function(frm){
   reset_sales_partner(frm)
   show_hide_ref_practitioner(frm);
   set_hmo_visibility(frm);

   frm.set_value("ref_practitioner","");
   frm.set_value("custom_practitioner_name","");

   var si_source = frm.doc.custom_source;
   if(si_source.includes("SC/PWD")){
        frm.set_value("additional_discount_percentage",20);
   }
   else if(si_source.includes("MD")){
        frm.set_value("additional_discount_percentage",10);
   }
   else{
       frm.set_value("additional_discount_percentage",0);
   }
});

frappe.ui.form.on('Sales Invoice', 'custom_hmo', function(frm){
    if(frm.doc.custom_hmo)
        frappe.call({
            "method": "frappe.client.get",
            "args":{
                doctype: "HMO",
                name: frm.doc.custom_hmo
            },
            "callback":function(data){
                var price_list = data.message['price_list'];

                frm.set_value("selling_price_list",price_list);
            }
        });
});

frappe.ui.form.on("Sales Invoice", "custom_source", function(frm) {
        practitioner_set_query(frm);

});

frappe.ui.form.on("Sales Invoice", "custom_calculate_vat", function(frm,cdt,cdn) {
    console.log("SI AMOUNT");
    var si_source = frm.doc.custom_source;
    var addtl_disc = parseFloat(frm.doc.custom_additional_discount);
    var net_of_vat = frm.doc.total/1.12;
    var vat = frm.doc.total - net_of_vat;
    var less_discount = net_of_vat * ((frm.doc.additional_discount_percentage+addtl_disc)/100);
    if(si_source.includes("SC/PWD")){
       frm.set_value("custom_vat_amount", vat);
       frm.set_value("custom_net_of_vat", net_of_vat);
       frm.set_value("custom_less_discount", less_discount);
       frm.set_value("custom_add_vat",0);
       frm.set_value("custom_amount_due", (net_of_vat - less_discount));
   }
   else{
       frm.set_value("custom_vat_amount", vat);
       frm.set_value("custom_net_of_vat", net_of_vat);
       frm.set_value("custom_less_discount", less_discount);
       frm.set_value("custom_add_vat", vat);
       frm.set_value("custom_amount_due", (net_of_vat - less_discount + vat));
   }
});

