    console.log("ADD");
    get_payment_balance(frm);
});

frappe.ui.form.on("Invoice Payment Table",'custom_invoice_payments_remove', function(frm){
    console.log("REMOVE")
    get_payment_balance(frm);
});

frappe.ui.form.on("Invoice Payment Table",'amount', function(frm,cdt,cdn){
    console.log("amount");
    update_payment_balance(frm);
});

frappe.ui.form.on("Invoice Payment Table",'ref_no', function(frm, cdt, cdn){
    var or_no = locals[cdt][cdn].ref_no;
    var no_space = or_no.replace(' ','');
    console.log(or_no, no_space);
    frappe.model.set_value(cdt,cdn,'ref_no',no_space);
    frm.refresh_field('custom_invoice_payments')


});

function practitioner_set_query(frm){

    var filters = {};

    if(frm.doc.custom_source.includes("Doctor's Referral")||frm.doc.custom_source.includes("Promo")){
        filters = {"custom_type": "Doctor (MD)"}
    }

    frm.set_query("ref_practitioner", function() {
        return {
            "filters": filters
        };
    });
}

function show_hide_ref_practitioner(frm){
    if(frm.doc.custom_source.includes("Doctor's Referral") ||frm.doc.custom_source.includes("Promo") || frm.doc.custom_source.includes("HMO")){
        frm.set_df_property("ref_practitioner", "hidden", 0);
        frm.set_df_property("ref_practitioner", "read_only", 0);
        if(!frm.doc.custom_source.includes("Promo")){
            frm.set_df_property("custom_practitioner_name", "reqd", 1);
            frm.set_df_property("ref_practitioner", "reqd", 1);
        }
        else{
            frm.set_df_property("custom_practitioner_name", "reqd", 0);
            frm.set_df_property("ref_practitioner", "reqd", 0);
        }

        frm.set_df_property("custom_practitioner_name", "hidden", 0);
        frm.set_df_property("custom_practitioner_name", "read_only", 0);

    }
    else{
        frm.set_value("ref_practitioner","");
        frm.set_df_property("ref_practitioner", "hidden", 1);
        frm.set_df_property("ref_practitioner", "read_only", 1);
        frm.set_df_property("ref_practitioner", "reqd", 0);
        frm.set_value("custom_practitioner_name","");
        frm.set_df_property("custom_practitioner_name", "hidden", 1);
        frm.set_df_property("custom_practitioner_name", "read_only", 1);
        frm.set_df_property("custom_practitioner_name", "reqd", 0);
    }
}

function set_hmo_visibility(frm){
    if(frm.doc.custom_source.includes("HMO")){
        frm.set_df_property("custom_hmo", "hidden", 0);
        frm.set_df_property("custom_hmo", "read_only", 0);
        frm.set_df_property("custom_hmo", "reqd", 1);
    }
    else{
        frm.set_df_property("custom_hmo", "hidden", 1);
        frm.set_df_property("custom_hmo", "read_only", 1);
        frm.set_df_property("custom_hmo", "reqd", 0);
    }

}

function reset_sales_partner(frm){
    frm.set_value("sales_partner","")
    frm.set_value("commission_rate",0);
    frm.set_value("total_commission",0);    
}

function get_payment_balance(frm){
    var total_payments = 0;
    var grand_total = frm.doc.custom_amount_due;
    var invoice_payments = frm.doc.custom_invoice_payments;
    var balance_amount = 0;

    for(var i=0; i<invoice_payments.length; i++){
        if(typeof invoice_payments[i]=="undefined"){
        }
        else{
            if("amount" in invoice_payments[i])
                if(invoice_payments[i].amount>0)
                    total_payments += invoice_payments[i].amount;
        }

    }

    balance_amount = grand_total - total_payments;

    for(var j=0; j<invoice_payments.length; j++){
        console.log(invoice_payments[j].amount+"| invoice_pmt amount");
        if((invoice_payments[j].amount == undefined)||(invoice_payments[j].amount==0))
            frappe.model.set_value(invoice_payments[j].doctype, invoice_payments[j].name, 'amount', balance_amount)
        frm.refresh_field('custom_invoice_payments');
    }

    update_payment_balance(frm);

}

function set_payment_balance(frm,balance){

}

function update_payment_balance(frm){
    var total_payments = 0;
    var grand_total = frm.doc.grand_total;
    var invoice_payments = frm.doc.custom_invoice_payments;
    var balance_amount = 0;

    for(var i=0; i<invoice_payments.length; i++){
            total_payments += invoice_payments[i].amount;
    }

    frm.set_value("custom_payments_total", total_payments);

}

