script:
frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		// your code here
	}
})

frappe.ui.form.on('Sales Invoice', 'ref_practitioner', function(frm){
    if(frm.doc.ref_practitioner)
        frappe.call({
            "method": "frappe.client.get",
            "args":{
                doctype: "Healthcare Practitioner",
                name: frm.doc.ref_practitioner
            },
            "callback":function(data){
                var practitioner_type = data.message['custom_type'];
                var practitioner_name = data.message['practitioner_name'];
                if(practitioner_type == 'Doctor (MD)'){
                    practitioner_name += ', MD';
                }
                frm.set_value("custom_practitioner_name",practitioner_name);
            }
        });
});
