css:
.print-format{
    width: 10.5cm !important;
    height: 20cm !important;
    min-height: 20cm !important;
    padding: 0.5cm 0.5cm 0.5cm 1.1cm;
    font-size: 11px !important;
}
.invoice-print{}
.date{
    width:100%;
    display: inline-block;
    margin-top: 2.7cm
}
.pdate{
    float:right;
}
.patient-name{
    margin-left: 1.7cm;
}

.receipt-body{
    margin-top:2.6cm;
    height:7.5cm;
    min-height:7.5cm;
    max-height:7.5cm;
    font-size:8px!important;
}
.receipt-body tr{
    height: 0.25cm;
    max-height: 0.25cm;
}
.receipt-body td{
    padding:0px!important;
}
.receipt-body .item-name{
    min-width: 5.55cm;
    max-width: 5.55cm;
    width: 5.55cm;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow:hidden;
}
.receipt-body .qty{
    min-width: 0.8cm;
    max-width: 0.8cm;
    width: 0.8cm;
}
.receipt-body .rate{
    min-width: 1.1cm;
    max-width: 1.1cm;
    width: 1.1cm; 
}
.receipt-body .amount{
    min-width: 1.4cm;
    max-width: 1.4cm;
    width: 1.4cm;
}

.vat-sales{
    display: block;
    float: left;
    width: 4.2cm;
    min-width: 4.2cm;
    max-width: 4.2cm;
}
.vat-sales div{
    height:0.5cm;
    margin-left: 2cm;    
}

.total-sales{
    display: block;
    float: right;
    width: 4.6cm;
    min-width: 4.6cm;
    max-width: 4.6cm;
}

.total-sales div{
    margin-left: 2.8cm;
    height:0.5cm;
}
